//CUSTOMIZE store credit US03

class Magestore_Customercredit_Model_System_Config_Backend_NotifyCustomer extends Mage_Core_Model_Config_Data
{
    /**
     * @return void
     */
    protected function _afterLoad()
    {
        if (!is_array($this->getValue())) {
            $value = $this->getValue();
            $this->setValue(empty($value) ? false : unserialize($value));
        }
        return parent::_afterLoad();
    }

    /**
     * Processing object before save data
     *
     * @return Mage_Core_Model_Abstract
     */
    protected function _beforeSave()
    {
        if (is_array($this->getValue())) {
            $value = $this->getValue();
            unset($value['__empty']);
            $this->setValue(serialize($value));
        }
        return parent::_beforeSave();
    }

    protected function _afterSave()
    {
        if ($this->isValueChanged()) {
            Mage::helper('customercredit')->setCreditExpirationNotifyDateAfterChangeConfig($this->getValue());
        }
    }
}
